import React, { useState, useEffect } from "react"
import { useHistory } from 'react-router-dom';
import { firestore } from '../components/Firebase';
import { collection, addDoc, getDocs, query, where, onSnapshot } from "firebase/firestore";



const Home = () => {
    const [climate, setClimate] = useState([])
    const [currentCity, setcurrentCity] = useState('')
    const [currentCityClimate, setcurrentCityClimate] = useState([])

    const change = async (event) => {
        setcurrentCity(event.target.value)
        DisplayData()

    }

    //get all climates from firebase db
      const FetchClimates = async () => {
        await getDocs(collection(firestore, "cityClimate"))
            .then((querySnapshot) => {
                const newData = querySnapshot.docs  
                    .map((doc) => ({...doc.data(), id: doc.id}));
                setClimate(newData)
                console.log("new data: ", newData)

            })
    }

    const DisplayData =  () => {
        for (let i =0; i<=climate.length-1; i++){

            //if value is found, iterate though object and create a table 
            if(climate[i].city == currentCity){
                console.log('success!', climate[i].result)
                for (const [key, value] of Object.entries(climate[i].result)) {
                    console.log(key, value);
                    // setcurrentCityClimate(climate[i].result)
                  }
            }
            else{
                console.log('failure')
            }
            }
    }

      // Start the fetch operation as soon as
    // the page loads
    useEffect(() => {
        FetchClimates();
        DisplayData();
    }, [currentCity, currentCityClimate])


    return (
    <>
        <section id='about'>
        <h3>About</h3>
        <p>Random Latin filler words...</p>
    </section>

    <section id='get-started'>
        <h3>Get Started!</h3>
        <p>Random Latin filler words....</p>

        <div className="column" id="left-column">   
            <div id ='map'>****Map Goes Here****</div>
        </div>

        <p>Select a city and country and get started!</p>

        <select
        onChange={((event) => setcurrentCity(event.target.value))}
        >
        <option>City</option>
        {climate.map((obj) =>(
            <option
                className="item"
                key = {obj.id}
                >
                    {obj.city}
            </option>
        ))}
        </select>
        <br/>
        <br/>


        <div>See Climate Data Here:
        {currentCity}
        {/* {} */}
        </div>

        <br/>
        <button >Go</button>

    </section>
  </>
);
}

export default Home;
