import React, { useState, useEffect } from "react"
import { useHistory } from 'react-router-dom';
import { firestore } from '../components/Firebase';
import { collection, addDoc, getDocs, query, where, onSnapshot } from "firebase/firestore";



const Home = () => {
    const [climate, setClimate] = useState([])
    const [currentCity, setcurrentCity] = useState('EX: City Climate Data')
    const [currentCityClimate, setcurrentCityClimate] = useState([])


    //get all climates from firebase db
      const FetchClimates = async () => {
        await getDocs(collection(firestore, "cityClimate"))
            .then((querySnapshot) => {
                const newData = querySnapshot.docs  
                    .map((doc) => ({...doc.data(), id: doc.id}));
                setClimate(newData)
                console.log("new data: ", newData)

            })
    }

    const DisplayData = async () => {
        const q = query(
            collection(firestore, "cityClimate"),
            where('state', '==', 'Washington')
            )

        const unsub = onSnapshot(q, querySnapshot => {
            const items = [];
            querySnapshot.forEach((doc) =>{
            items.push(doc.data());
            });
            setcurrentCityClimate(items);
        });
        console.log("unsub: ", unsub)
        return () => {
            unsub();
        }

    }

      // Start the fetch operation as soon as
    // the page loads
    useEffect(() => {
        FetchClimates();

        DisplayData();

    }, [])
    console.log("new climate: ", climate)

    return (
    <>
        <section id='about'>
        <h3>About</h3>
        <p>Random Latin filler words...</p>
    </section>

    <section id='get-started'>
        <h3>Get Started!</h3>
        <p>Random Latin filler words....</p>

        <div className="column" id="left-column">   
            <div id ='map'>****Map Goes Here****</div>
        </div>

        <p>Select a city and country and get started!</p>

        <select
        onChange={((event) => setcurrentCity(event.target.value))}
        >
        <option>City</option>
        {climate.map((obj) =>(
            <option
                className="item"
                key = {obj.id}
                >
                    {obj.city}, {obj.state}
            </option>
        ))}
        </select>
        <br/>
        <br/>


        <div>See Climate Data Here:
        {currentCity}
        </div>

        <br/>
        <button >Go</button>

    </section>
  </>
);
}

export default Home;
