import React, { useState, useEffect } from "react"
import { useHistory } from 'react-router-dom';
import { firestore } from '../components/Firebase';
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";



const Home = () => {
    const [climate, setClimate] = useState([])
    const [currentCity, setcurrentCity] = useState('Seattle')
    const [currentCityClimate, setcurrentCityClimate] = useState([])


    //get all climates from firebase db
      const FetchClimates = async () => {
        await getDocs(collection(firestore, "cityClimate"))
            .then((querySnapshot) => {
                const newData = querySnapshot.docs  
                    .map((doc) => ({...doc.data(), id: doc.id}));
                setClimate(newData)
                console.log("new data: ", newData)

            })
    }

    const DisplayData = async() => {
        // const newclimate = climate.filter((obj) => obj.city == currentCity)
        // // console.log("newclimate", newclimate)
        // setcurrentCityClimate(newclimate)
        // const citiesRef = collection(firestore, "cityClimate");
        // const q = query(citiesRef, where("city", "==", currentCity));

        // const querySnapshot = async () => {await getDocs(q)};
        // console.log(querySnapshot)
        // // querySnapshot.forEach((doc) => {
        // // // doc.data() is never undefined for query doc snapshots
        // // console.log(doc.id, " => ", doc.data());
        // // });

//         const q = collection((firestore, "cityClimate"), where("capital", "==", true));

//         const querySnapshot = await getDocs(q);
//         querySnapshot.forEach((doc) => {
//         // doc.data() is never undefined for query doc snapshots
//         console.log(doc.id, " => ", doc.data());
// });
        return (
            <div>{currentCity} </div>
        )
    }

      // Start the fetch operation as soon as
    // the page loads
    useEffect(() => {
        FetchClimates();
    }, [])
    console.log("new climate: ", climate)

    return (
    <>
        <section id='about'>
        <h3>About</h3>
        <p>Random Latin filler words...</p>
    </section>

    <section id='get-started'>
        <h3>Get Started!</h3>
        <p>Random Latin filler words....</p>

        <div className="column" id="left-column">   
            <div id ='map'>****Map Goes Here****</div>
        </div>

        <p>Select a city and country and get started!</p>

        <select
        onChange={((event) => setcurrentCity(event.target.value))}
        >
        <option>City</option>
        {climate.map((obj) =>(
            <option
                className="item"
                key = {obj.id}
                >
                    {obj.city}, {obj.state}
            </option>
        ))}
        </select>
        <br/>
        <br/>


        <div>See Climate Data Here:
        {/* {climate
            .filter((obj) => obj.city == currentCity)
            .map((obj) => {
                <div>
                    {obj}
                </div>
            })
        }     */}
        <DisplayData/>
        </div>

        <br/>
        <button>Go</button>

    </section>
  </>
);
}

export default Home;
